package com.urooj.carpoolingapp.driverui.fragment.RequestReceive;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AlertDialog;
import androidx.fragment.app.Fragment;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.database.*;
import com.urooj.carpoolingapp.R;

public class RequestNotificationsFragment extends Fragment {

 private DatabaseReference rideRequestsRef;
 private String driverId;

 @Override
 public void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);

 // Initialize Firebase
 driverId = FirebaseAuth.getInstance().getCurrentUser().getUid();
 rideRequestsRef = FirebaseDatabase.getInstance().getReference("ride_requests");
 }

 @Override
 public View onCreateView(LayoutInflater inflater, ViewGroup container,
 Bundle savedInstanceState) {
 return inflater.inflate(R.layout.fragment_request_notifications, container, false);
 }

 @Override
 public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
 super.onViewCreated(view, savedInstanceState);

 listenForRideRequests();
 }

 private void listenForRideRequests() {
 rideRequestsRef.orderByChild("driverId").equalTo(driverId)
 .addChildEventListener(new ChildEventListener() {
 @Override
 public void onChildAdded(DataSnapshot snapshot, String previousChildName) {
 if (snapshot.exists()) {
 String status = snapshot.child("status").getValue(String.class);
 if ("pending".equals(status)) {
 showRideRequestDialog(snapshot);
 }
 }
 }

 @Override public void onChildChanged(DataSnapshot snapshot, String previousChildName) {}
 @Override public void onChildRemoved(DataSnapshot snapshot) {}
 @Override public void onChildMoved(DataSnapshot snapshot, String previousChildName) {}
 @Override public void onCancelled(DatabaseError error) {
 Toast.makeText(requireContext(), "Failed to load ride requests", Toast.LENGTH_SHORT).show();
 }
 });
 }

 private void showRideRequestDialog(DataSnapshot snapshot) {
 String passengerId = snapshot.child("passengerId").getValue(String.class);

 new AlertDialog.Builder(requireContext())
 .setTitle("New Ride Request")
 .setMessage("You have a new ride request from Passenger: " + passengerId)
 .setPositiveButton("Accept", (dialog, which) -> {
 snapshot.getRef().child("status").setValue("accepted");
 Toast.makeText(requireContext(), "Ride Accepted", Toast.LENGTH_SHORT).show();
 })
 .setNegativeButton("Reject", (dialog, which) -> {
 snapshot.getRef().child("status").setValue("rejected");
 Toast.makeText(requireContext(), "Ride Rejected", Toast.LENGTH_SHORT).show();
 })
 .setCancelable(false)
 .show();
 }
}

