+ public boolean onAnnotationClick(@NonNull PointAnnotation clickedAnnotation) {
+ JsonElement data = clickedAnnotation.getData();
+ if (data != null) {
+ String driverId = new Gson().fromJson(data, String.class);
+ Log.d("ANNOTATION_CLICK", "Driver ID clicked: " + driverId);
+
+ // Open the driver's profile
+ openDriverProfile(driverId);
+ return true;
+ }
+ return false;
 }
 });
+ addOnMapClickListener(mapView.getMapboxMap(), new OnMapClickListener() {
+ @Override
+ public boolean onMapClick(@NonNull Point destinationPoint) {
+ // Delete only the destination marker, not driver markers
+ // Find and remove any existing destination markers
+ List<PointAnnotation> annotationsToDelete = new java.util.ArrayList<>();
+ for (PointAnnotation annotation : pointAnnotationManager.getAnnotations()) {
+ // Check if this is a destination marker (no driver ID data)
+ if (annotation.getData() == null) {
+ annotationsToDelete.add(annotation);
+ }
+ }
+ pointAnnotationManager.delete(annotationsToDelete);
+
+ // Add new destination marker
+ PointAnnotationOptions pointAnnotationOptions = new PointAnnotationOptions()
+ .withTextAnchor(TextAnchor.CENTER)
+ .withIconImage(bitmap)
+ .withPoint(destinationPoint);
+ pointAnnotationManager.create(pointAnnotationOptions);
+
+ // Get last known location from navigationLocationProvider
+ Location currentLocation = navigationLocationProvider.getLastLocation();
+ if (currentLocation != null) {
+ Point originPoint = Point.fromLngLat(currentLocation.getLongitude(), currentLocation.getLatitude());
+
+ setRoute.setOnClickListener(new View.OnClickListener() {
+ @Override
+ public void onClick(View view) {
+ fetchRoute(originPoint, destinationPoint);
+ }
+ });
+ } else {
+ Toast.makeText(getContext(), "Current location not available", Toast.LENGTH_SHORT).show();
+ }
+
+ return true;
+ }
+ });
+
+ focusLocationBtn.setOnClickListener(new View.OnClickListener() {
+ @Override
+ public void onClick(View view) {
+ focusLocation = true;
+ getGestures(mapView).addOnMoveListener(onMoveListener);
+ focusLocationBtn.hide();
+ }
+ });
 }
