 private void fetchDriverData() {
 loadingProgressBar.setVisibility(View.VISIBLE);

 // First get location data from driverShareLocation
 DatabaseReference locationRef = FirebaseDatabase.getInstance()
 .getReference("driverShareLocation")
 .child(driverId);

 Log.d("ViewDriverProfile", "Fetching location data for driver ID: " + driverId);

 locationRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 if (snapshot.exists()) {
 String name = snapshot.child("name").exists() ? snapshot.child("name").getValue(String.class) : "Driver";
 Double latitude = snapshot.child("latitude").exists() ? snapshot.child("latitude").getValue(Double.class) : null;
 Double longitude = snapshot.child("longitude").exists() ? snapshot.child("longitude").getValue(Double.class) : null;

 // Update the name field immediately
 if (nameTextView != null) {
 nameTextView.setText(name);
 }

 if (latitude != null && longitude != null) {
 if (addressTextView != null) {
 addressTextView.setText("Current Location: " + latitude + ", " + longitude);
 }
 }

 fetchProfileData();
 } else {
 fetchProfileData();
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 Log.e("ViewDriverProfile", "Error fetching location data: " + error.getMessage());
 fetchProfileData();
 }
 });
 }

 private void fetchProfileData() {
 // Get detailed profile data from users folder
 DatabaseReference profileRef = FirebaseDatabase.getInstance()
 .getReference("users")
 .child(driverId);

 Log.d("ViewDriverProfile", "Fetching profile data for driver ID: " + driverId);

 profileRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 if (loadingProgressBar != null) {
 loadingProgressBar.setVisibility(View.GONE);
 } else {
 Log.e("ViewDriverProfile", "loadingProgressBar is null, cannot hide it");
 }

 if (snapshot.exists()) {
 Log.d("ViewDriverProfile", "Profile data found");

 // Extract profile data
 String address = snapshot.child("address").exists() ?
 snapshot.child("address").getValue(String.class) : null;
 String carNumber = snapshot.child("carNumber").exists() ?
 snapshot.child("carNumber").getValue(String.class) : null;
 String caste = snapshot.child("caste").exists() ?
 snapshot.child("caste").getValue(String.class) : null;
 String name = snapshot.child("name").exists() ?
 snapshot.child("name").getValue(String.class) : null;
 String phoneNumber = snapshot.child("phoneNumber").exists() ?
 snapshot.child("phoneNumber").getValue(String.class) : null;

 // Only update name if it wasn't already set and we have a value
 if (name != null && nameTextView != null && nameTextView.getText().toString().equals("Driver")) {
 nameTextView.setText(name);
 } else if (nameTextView == null) {
 Log.e("ViewDriverProfile", "nameTextView is null, cannot update name");
 }

 // Update the address if we have a proper one (not coordinates)
 if (address != null && addressTextView != null) {
 addressTextView.setText(address);
 } else if (addressTextView == null) {
 Log.e("ViewDriverProfile", "addressTextView is null, cannot update address");
 }

 if (carNumberTextView != null) {
 carNumberTextView.setText(carNumber != null ? carNumber : "Not provided");
 } else {
 Log.e("ViewDriverProfile", "carNumberTextView is null, cannot update car number");
 }

 if (casteTextView != null) {
 casteTextView.setText(caste != null ? caste : "Not provided");
 } else {
 Log.e("ViewDriverProfile", "casteTextView is null, cannot update caste");
 }
 if (phoneNumberTextView != null) {
 phoneNumberTextView.setText(phoneNumber != null ? phoneNumber : "Not provided");
 } else {
 Log.e("ViewDriverProfile", "phoneNumberTextView is null, cannot update phone number");
 }


 // Load profile image from storage
 loadProfileImage();
 } else {
 Log.e("ViewDriverProfile", "Profile data not found for driver ID: " + driverId);
 // Keep whatever data we already have displayed
 if (addressTextView != null && !addressTextView.getText().toString().contains("Location")) {
 addressTextView.setText("No address information available");
 } else if (addressTextView == null) {
 Log.e("ViewDriverProfile", "addressTextView is null, cannot update address");
 }
 if (carNumberTextView != null) {
 carNumberTextView.setText("Not provided");
 } else {
 Log.e("ViewDriverProfile", "carNumberTextView is null, cannot update car number");
 }
 if (casteTextView != null) {
 casteTextView.setText("Not provided");
 } else {
 Log.e("ViewDriverProfile", "casteTextView is null, cannot update caste");
 }
 if (loadingProgressBar != null) {
 loadingProgressBar.setVisibility(View.GONE);
 } else {
 Log.e("ViewDriverProfile", "loadingProgressBar is null, cannot hide it");
 }
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 if (loadingProgressBar != null) {
 loadingProgressBar.setVisibility(View.GONE);
 } else {
 Log.e("ViewDriverProfile", "loadingProgressBar is null, cannot hide it");
 }
 Log.e("ViewDriverProfile", "Error fetching profile data: " + error.getMessage());
 Toast.makeText(getContext(), "Failed to load driver profile data", Toast.LENGTH_SHORT).show();
 }
 });
 }

 private void loadProfileImage() {
 StorageReference storageRef = FirebaseStorage.getInstance().getReference()
 .child("profile_images").child(driverId);

 storageRef.getDownloadUrl().addOnSuccessListener(uri -> {
 if (profileImageView != null) {
 Picasso.get().load(uri)
 .placeholder(R.drawable.ic_profile_placeholder)
 .error(R.drawable.ic_profile_placeholder)
 .into(profileImageView);
 } else {
 Log.e("ViewDriverProfile", "profileImageView is null, cannot load profile image");
 }
 }).addOnFailureListener(e -> {
 // Use default image if profile image not available
 if (profileImageView != null) {
 profileImageView.setImageResource(R.drawable.ic_profile_placeholder);
 } else {
 Log.e("ViewDriverProfile", "profileImageView is null, cannot set default image");
 }
 });
 }

 private void checkExistingRideRequest() {
 String passengerId = FirebaseAuth.getInstance().getCurrentUser().getUid();
 DatabaseReference rideRequestsRef = FirebaseDatabase.getInstance().getReference("ride_requests");

 rideRequestsRef.orderByChild("passengerId").equalTo(passengerId)
 .addValueEventListener(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 boolean requestFound = false;

 for (DataSnapshot requestSnapshot : snapshot.getChildren()) {
 String requestDriverId = requestSnapshot.child("driverId").getValue(String.class);

 if (driverId.equals(requestDriverId)) {
 requestFound = true;
 currentRequestId = requestSnapshot.getKey();
 currentRequestStatus = requestSnapshot.child("status").getValue(String.class);
 updateRequestButton(currentRequestStatus);
 break;
 }
 }

 if (!requestFound && requestRideButton != null) {
 requestRideButton.setText("Request Ride");
 requestRideButton.setEnabled(true);
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 Log.e("ViewDriverProfile", "Error checking ride requests: " + error.getMessage());
 }
 });
 }

 private void updateRequestButton(String status) {
 if (requestRideButton == null) return;

 switch (status) {
 case "pending":
 requestRideButton.setText("Request Pending");
 requestRideButton.setEnabled(false);
 break;
 case "accepted":
 requestRideButton.setText("Request Accepted");
 requestRideButton.setEnabled(false);
 break;
 case "rejected":
 requestRideButton.setText("Request Rejected");
 requestRideButton.setEnabled(true);
 requestRideButton.setOnClickListener(v -> requestRide());
 break;
 default:
 requestRideButton.setText("Request Ride");
 requestRideButton.setEnabled(true);
 }
 }

 private void requestRide() {
 // Check connectivity first
 DatabaseReference connectedRef = FirebaseDatabase.getInstance().getReference(".info/connected");
 connectedRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 Boolean connected = snapshot.getValue(Boolean.class);
 if (connected != null && connected) {
 proceedWithRequest();
 } else {
 Toast.makeText(getContext(), "No internet connection. Please try again when online.", Toast.LENGTH_LONG).show();
 Log.e("ViewDriverProfile", "Failed to send request: No internet connection");
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 Toast.makeText(getContext(), "Connection check failed. Please try again.", Toast.LENGTH_SHORT).show();
 Log.e("ViewDriverProfile", "Connection check failed: " + error.getMessage());
 }
 });
 }

 private void proceedWithRequest() {
 DatabaseReference rideRequestsRef = FirebaseDatabase.getInstance().getReference("ride_requests");
 String requestId = rideRequestsRef.push().getKey();

 // Check if user is authenticated
 if (FirebaseAuth.getInstance().getCurrentUser() == null) {
 Toast.makeText(getContext(), "You must be logged in to request a ride", Toast.LENGTH_SHORT).show();
 Log.e("ViewDriverProfile", "Failed to send request: User not authenticated");
 return;
 }

 String passengerId = FirebaseAuth.getInstance().getCurrentUser().getUid();

 Log.d("ViewDriverProfile", "Attempting to send ride request. Driver ID: " + driverId);
 Log.d("ViewDriverProfile", "Passenger ID: " + passengerId);
 Log.d("ViewDriverProfile", "Database path: " + rideRequestsRef.toString());

 Map<String, Object> requestData = new HashMap<>();
 requestData.put("passengerId", passengerId);
 requestData.put("driverId", driverId);
 requestData.put("status", "pending");
 requestData.put("timestamp", System.currentTimeMillis());

 if (requestId != null) {
 // Show a toast indicating the request is being processed
 Toast.makeText(getContext(), "Sending ride request...", Toast.LENGTH_SHORT).show();

 rideRequestsRef.child(requestId).setValue(requestData)
 .addOnSuccessListener(aVoid -> {
 Log.d("ViewDriverProfile", "Ride request sent successfully. Request ID: " + requestId);

 // Also create a notification entry for the driver
 createDriverNotification(requestId, driverId, passengerId);

 Toast.makeText(getContext(), "Ride request sent to driver", Toast.LENGTH_SHORT).show();
 currentRequestId = requestId;
 currentRequestStatus = "pending";
 updateRequestButton("pending");
 })
 .addOnFailureListener(e -> {
 Log.e("ViewDriverProfile", "Failed to send request: " + e.getMessage(), e);
 Toast.makeText(getContext(), "Failed to send request: " + e.getMessage(),
 Toast.LENGTH_SHORT).show();
 });
 } else {
 Log.e("ViewDriverProfile", "Failed to create request ID");
 Toast.makeText(getContext(), "Failed to create request ID", Toast.LENGTH_SHORT).show();
 }
 }

 private void createDriverNotification(String requestId, String driverId, String passengerId) {
 // Get passenger name
 DatabaseReference userRef = FirebaseDatabase.getInstance().getReference("users").child(passengerId);
 userRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 String passengerName = "A passenger";
 if (snapshot.exists() && snapshot.child("fullName").exists()) {
 passengerName = snapshot.child("fullName").getValue(String.class);
 }

 // Create notification data
 DatabaseReference notificationsRef = FirebaseDatabase.getInstance().getReference("driver_notifications");
 String notificationId = notificationsRef.push().getKey();

 if (notificationId != null) {
 Map<String, Object> notificationData = new HashMap<>();
 notificationData.put("driverId", driverId);
 notificationData.put("title", "New Ride Request");
 notificationData.put("message", passengerName + " has requested a ride");
 notificationData.put("requestId", requestId);
 notificationData.put("timestamp", System.currentTimeMillis());
 notificationData.put("read", false);

 notificationsRef.child(notificationId).setValue(notificationData)
 .addOnSuccessListener(aVoid -> {
 Log.d("ViewDriverProfile", "Driver notification created successfully");
 })
 .addOnFailureListener(e -> {
 Log.e("ViewDriverProfile", "Failed to create driver notification: " + e.getMessage());
 });
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 Log.e("ViewDriverProfile", "Failed to get passenger details: " + error.getMessage());
 }
 });
 }
}
