+ public void onCancelled(@NonNull DatabaseError error) {
+ Log.e("ViewDriverProfile", "Failed to fetch driver location data", error.toException());
+ Toast.makeText(ViewDriverProfile.this, "Failed to load driver data", Toast.LENGTH_SHORT).show();
+ progressBar.setVisibility(View.GONE);
+ }
+ });
+ }
+
+ private void fetchProfileData() {
+ DatabaseReference userRef = FirebaseDatabase.getInstance()
+ .getReference("users")
+ .child(driverId);
+
+ userRef.addListenerForSingleValueEvent(new ValueEventListener() {
+ @Override
+ public void onDataChange(@NonNull DataSnapshot snapshot) {
+ progressBar.setVisibility(View.GONE);
+
+ if (snapshot.exists()) {
+ // Get name (if not already fetched from location)
+ if (nameTextView.getText().toString().isEmpty()) {
+ String fullName = snapshot.child("fullName").getValue(String.class);
+ if (fullName == null || fullName.isEmpty()) {
+ fullName = snapshot.child("name").getValue(String.class);
+ }
+ if (fullName != null && !fullName.isEmpty()) {
+ nameTextView.setText(fullName);
+ }
+ }
+
+ // Get other profile information
+ String phone = snapshot.child("phone").getValue(String.class);
+ String carNumber = snapshot.child("carNumber").getValue(String.class);
+ String carType = snapshot.child("carType").getValue(String.class);
+ String address = snapshot.child("address").getValue(String.class);
+
+ if (phone != null) phoneTextView.setText(phone);
+ if (carNumber != null) carNumberTextView.setText(carNumber);
+ if (carType != null) carTypeTextView.setText(carType);
+ if (address != null) addressTextView.setText(address);
+
+ // Load profile image
+ loadProfileImage();
+
+ } else {
+ Toast.makeText(ViewDriverProfile.this, "Driver profile not found", Toast.LENGTH_SHORT).show();
+ }
+ }
+
+ @Override
+ public void onCancelled(@NonNull DatabaseError error) {
+ progressBar.setVisibility(View.GONE);
+ Log.e("ViewDriverProfile", "Failed to fetch profile data", error.toException());
+ Toast.makeText(ViewDriverProfile.this, "Failed to load profile data", Toast.LENGTH_SHORT).show();
+ }
+ });
+ }
+
+ private void loadProfileImage() {
+ StorageReference storageRef = FirebaseStorage.getInstance().getReference()
+ .child("profile_images")
+ .child(driverId + ".jpg");
+
+ storageRef.getDownloadUrl().addOnSuccessListener(uri -> {
+ Glide.with(ViewDriverProfile.this)
+ .load(uri)
+ .placeholder(R.drawable.profile_placeholder)
+ .error(R.drawable.profile_placeholder)
+ .circleCrop()
+ .into(profileImageView);
+ }).addOnFailureListener(e -> {
+ Log.e("ViewDriverProfile", "Failed to load profile image", e);
+ // Use default image if loading fails
+ profileImageView.setImageResource(R.drawable.profile_placeholder);
+ });
+ }
+
+ private void setupRequestRideButton() {
+ requestRideButton.setOnClickListener(v -> {
+ String passengerId = FirebaseAuth.getInstance().getCurrentUser().getUid();
+ DatabaseReference rideRequestRef = FirebaseDatabase.getInstance()
+ .getReference("rideRequests")
+ .child(driverId)
+ .child(passengerId);
+
+ rideRequestRef.child("status").setValue("pending")
+ .addOnSuccessListener(aVoid -> {
+ Toast.makeText(ViewDriverProfile.this, "Ride request sent to driver", Toast.LENGTH_SHORT).show();
+ requestRideButton.setText("Request Sent");
+ requestRideButton.setEnabled(false);
+ })
+ .addOnFailureListener(e -> {
+ Toast.makeText(ViewDriverProfile.this, "Failed to send request: " + e.getMessage(), Toast.LENGTH_SHORT).show();
+ });
+ });
+ }
 }
