 private void updatePassengerLocationToFirebase(Location location) {
 if (location == null) {
 Log.e("FIREBASE", "Cannot update null location");
 return;
 }

 // Make sure we have a logged in user
 if (FirebaseAuth.getInstance().getCurrentUser() == null) {
 Log.e("FIREBASE", "No authenticated user found");
 return;
 }

 String passengerId = FirebaseAuth.getInstance().getCurrentUser().getUid();

 // Use passengerLocation path instead of rides
 DatabaseReference ref = FirebaseDatabase.getInstance().getReference("passengerLocation");

 Map<String, Object> passengerData = new HashMap<>();
 passengerData.put("latitude", location.getLatitude());
 passengerData.put("longitude", location.getLongitude());
 passengerData.put("name", "Passenger");
 passengerData.put("timestamp", System.currentTimeMillis());

 // Check Firebase connection status before updating
 DatabaseReference connectedRef = FirebaseDatabase.getInstance().getReference(".info/connected");
 connectedRef.addValueEventListener(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 Boolean connected = snapshot.getValue(Boolean.class);
 if (connected != null && connected) {
 // Connected to Firebase, proceed with update
 updateLocation(ref, passengerId, passengerData);
 } else {
 Log.e("FIREBASE", "No connection to Firebase");
 // Don't show repeated toast messages
 // Toast.makeText(getContext(), "No connection to Firebase", Toast.LENGTH_SHORT).show();
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 Log.e("FIREBASE", "Connection check error: " + error.getMessage());
 }
 });
 }

 private void updateLocation(DatabaseReference ref, String passengerId, Map<String, Object> data) {
 ref.child(passengerId).setValue(data)
 .addOnSuccessListener(aVoid -> {
 Log.d("FIREBASE", "Passenger location updated successfully");
 })
 .addOnFailureListener(e -> {
 Log.e("FIREBASE", "Failed to update location: " + e.getMessage(), e);

 DatabaseReference fallbackRef = FirebaseDatabase.getInstance().getReference("rides");
 fallbackRef.child(passengerId).setValue(data)
 .addOnSuccessListener(aVoid -> Log.d("FIREBASE", "Passenger location updated via fallback"))
 .addOnFailureListener(fallbackError -> Log.e("FIREBASE", "Fallback also failed: " + fallbackError.getMessage()));
 });
 }

 private void openDriverProfile(String driverId) {
 if (getActivity() != null) {
 Log.d("SharedRideFragment", "Opening driver profile with ID: " + driverId);
 Intent intent = new Intent(getActivity(), ViewDriverProfile.class);
 intent.putExtra("DRIVER_ID", driverId);
 startActivity(intent);
 Toast.makeText(getContext(), "Opening driver profile...", Toast.LENGTH_SHORT).show();
 }
 }

 private void updateCamera(Point point, Double bearing) {
 MapAnimationOptions animationOptions = new MapAnimationOptions.Builder().duration(1500L).build();
 CameraOptions cameraOptions = new CameraOptions.Builder()
 .center(point)
 .zoom(18.0)
 .bearing(bearing)
 .pitch(45.0)
 .padding(new EdgeInsets(1000.0, 0.0, 0.0, 0.0))
 .build();

 getCamera(mapView).easeTo(cameraOptions, animationOptions);
 }

 @SuppressLint("MissingPermission")
 private void fetchRoute(Point originPoint, Point destinationPoint) {
 RouteOptions routeOptions = RouteOptions.builder()
 .coordinatesList(Arrays.asList(originPoint, destinationPoint))
 .profile(DirectionsCriteria.PROFILE_DRIVING)
 .bearingsList(Arrays.asList(
 Bearing.builder()
 .angle(0.0) // optional
 .degrees(45.0)
 .build(),
 null
 ))
 .overview(DirectionsCriteria.OVERVIEW_FULL)
 .steps(true)
 .voiceInstructions(true)
 .bannerInstructions(true)
 .language("en")
 .continueStraight(true)
 .build();

 setRoute.setEnabled(false);
 setRoute.setText("Fetching route...");

 // First clear any existing routes
 mapboxNavigation.setNavigationRoutes(Collections.emptyList());

 mapboxNavigation.requestRoutes(routeOptions, new NavigationRouterCallback() {
 @Override
 public void onRoutesReady(@NonNull List<NavigationRoute> routes, @NonNull RouterOrigin routerOrigin) {
 mapboxNavigation.setNavigationRoutes(routes);
 saveRouteToFirebase(originPoint, destinationPoint);
 focusLocationBtn.performClick();
 setRoute.setEnabled(true);
 setRoute.setText("Route Set");

 // Reset points to allow for new route selection
 SharedRideFragment.this.originPoint = null;
 SharedRideFragment.this.destinationPoint = null;
 }

 @Override
 public void onFailure(@NonNull List<RouterFailure> reasons, @NonNull RouteOptions routeOptions) {
 for (RouterFailure failure : reasons) {
 Log.e("ROUTE_FAILURE", failure.getMessage());
 }
 Toast.makeText(getContext(), "Route request failed", Toast.LENGTH_SHORT).show();
 setRoute.setEnabled(true);
 setRoute.setText("Set route");
 }

 @Override
 public void onCanceled(@NonNull RouteOptions routeOptions, @NonNull RouterOrigin routerOrigin) {
 Log.w("ROUTE_CANCELED", "Route request was canceled");
 }
 });
 }

 private void saveRouteToFirebase(Point origin, Point destination) {
 DatabaseReference ref = FirebaseDatabase.getInstance().getReference("Routes");
 String passengerId = FirebaseAuth.getInstance().getCurrentUser().getUid();

 Map<String, Object> routeData = new HashMap<>();
 routeData.put("originLat", origin.latitude());
 routeData.put("originLng", origin.longitude());
 routeData.put("destinationLat", destination.latitude());
 routeData.put("destinationLng", destination.longitude());
 routeData.put("timestamp", System.currentTimeMillis()); // Add timestamp to track latest route

 // Add additional passenger info for better context
 if (FirebaseAuth.getInstance().getCurrentUser() != null) {
 DatabaseReference userRef = FirebaseDatabase.getInstance().getReference("users")
 .child(FirebaseAuth.getInstance().getCurrentUser().getUid());
 userRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 if (snapshot.exists()) {
 // Try to get the passenger's name using both possible field names
 String passengerName;
 if (snapshot.child("fullName").exists()) {
 passengerName = snapshot.child("fullName").getValue(String.class);
 } else if (snapshot.child("name").exists()) {
 passengerName = snapshot.child("name").getValue(String.class);
 } else {
 passengerName = "Unknown Passenger";
 }

 routeData.put("passengerName", passengerName);
 updateRouteInFirebase(ref, passengerId, routeData);
 } else {
 // If user profile not found, proceed without name
 updateRouteInFirebase(ref, passengerId, routeData);
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // On error, proceed without name
 updateRouteInFirebase(ref, passengerId, routeData);
 }
 });
 } else {
 // If no user is logged in (shouldn't happen), proceed anyway
 updateRouteInFirebase(ref, passengerId, routeData);
 }
 }

 private void updateRouteInFirebase(DatabaseReference ref, String passengerId, Map<String, Object> routeData) {
 // Check if database rules allow writing to the Routes node
 ref.child(passengerId).get().addOnCompleteListener(task -> {
 if (task.isSuccessful()) {
 DataSnapshot snapshot = task.getResult();
 if (snapshot.exists()) {
 // If the node already exists, update it
 ref.child(passengerId).setValue(routeData)
 .addOnSuccessListener(aVoid -> {
 Log.d("FIREBASE", "Route saved to Routes/ successfully");
 if (getContext() != null) {
 Toast.makeText(getContext(), "Route updated", Toast.LENGTH_SHORT).show();
 }
 })
 .addOnFailureListener(e -> {
 Log.e("FIREBASE", "Failed to save route", e);
 if (getContext() != null) {
 Toast.makeText(getContext(), "Failed to update route: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 });
 } else {
 // If the node does not exist, check database rules
 ref.child(passengerId).setValue(routeData)
 .addOnSuccessListener(aVoid -> {
 Log.d("FIREBASE", "Route saved to Routes/ successfully");
 if (getContext() != null) {
 Toast.makeText(getContext(), "Route updated in database", Toast.LENGTH_SHORT).show();
 }
 })
 .addOnFailureListener(e -> {
 Log.e("FIREBASE", "Failed to save route", e);
 if (getContext() != null) {
 Toast.makeText(getContext(), "Failed to update route: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 });
 }
 } else {
 Log.e("FIREBASE", "Failed to check database rules", task.getException());
 }
 });
 }

 @Override
 public void onDestroyView() {
 super.onDestroyView();

 if (mapboxNavigation != null) {
 mapboxNavigation.unregisterRoutesObserver(routesObserver);
 mapboxNavigation.unregisterLocationObserver(locationObserver);
 mapboxNavigation.unregisterVoiceInstructionsObserver(voiceInstructionsObserver);

 mapboxNavigation.stopTripSession();

 mapboxNavigation.onDestroy();
 }

 if (routeLineApi != null) {
 routeLineApi.cancel();
 }
 if (routeLineView != null) {
 routeLineView.cancel();
 }
 if (mapboxVoiceInstructionsPlayer != null) {
 mapboxVoiceInstructionsPlayer.shutdown();
 }
 if (speechApi != null) {
 speechApi.cancel();
 }

 if (pointAnnotationManager != null) {
 pointAnnotationManager.deleteAll();
 }
 }
