app/src/main/java/com/urooj/carpoolingapp/passengerui/fragment/ViewDriverProfile.java
```java:app/src/main/java/com/urooj/carpoolingapp/passengerui/fragment/ViewDriverProfile.java
package com.urooj.carpoolingapp.passengerui.fragment;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import com.google.android.material.bottomsheet.BottomSheetDialogFragment;
import com.urooj.carpoolingapp.R;

public class ViewDriverProfile extends BottomSheetDialogFragment {

 private static final String ARG_DRIVER_ID = "driver_id";
 private String driverId;

 private TextView addressTextView, carNumberTextView, casteTextView;

 public static ViewDriverProfile newInstance(String driverId) {
 ViewDriverProfile fragment = new ViewDriverProfile();
 Bundle args = new Bundle();
 args.putString(ARG_DRIVER_ID, driverId);
 fragment.setArguments(args);
 return fragment;
 }

 @Override
 public void onCreate(@Nullable Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 if (getArguments() != null) {
 driverId = getArguments().getString(ARG_DRIVER_ID);
 }
 }

 @Nullable
 @Override
 public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
 @Nullable Bundle savedInstanceState) {
 View view = inflater.inflate(R.layout.fragment_view_driver_profile, container, false);

 addressTextView = view.findViewById(R.id.addressTextView);
 carNumberTextView = view.findViewById(R.id.carNumberTextView);
 casteTextView = view.findViewById(R.id.casteTextView);

 fetchDriverData();

 return view;
 }

 private void fetchDriverData() {
 DatabaseReference driverRef = FirebaseDatabase.getInstance()
 .getReference("users")
 .child(driverId);

 driverRef.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 if (snapshot.exists()) {
 String address = snapshot.child("address").getValue(String.class);
 String carNumber = snapshot.child("carNumber").getValue(String.class);
 String caste = snapshot.child("caste").getValue(String.class);

 addressTextView.setText(address != null ? address : "N/A");
 carNumberTextView.setText(carNumber != null ? carNumber : "N/A");
 casteTextView.setText(caste != null ? caste : "N/A");
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // Handle error if needed
 }
 });
 }
}

```
